#include<iostream>
#include<vector>
using namespace std;
  int col, row, number = 0;
    void dfs(vector<vector<char>>& binary_matrix, int x, int y) {
        if (x < 0 || x == col || y < 0 || y == row)
        {
        return;
        }
        char &currpos = binary_matrix[y][x];
        if (currpos != '1')   //checking if component is connected
        {
        return;
        } 
        currpos = '#';
        dfs(binary_matrix, x + 1, y);  //checking in all directions
        dfs(binary_matrix, x - 1, y);
        dfs(binary_matrix, x, y + 1);
        dfs(binary_matrix, x, y - 1);
    }
    int count(vector<vector<char>>& binary_matrix) {
        row = binary_matrix.size(); 
        if (!row) return number;  //no input then return
        col = binary_matrix[0].size();
        for (int y = 0; y < row; y++) {
            for (int x = 0; x < col; x++) {
                if (binary_matrix[y][x] == '1') {
                    number++;
                    dfs(binary_matrix, x, y);
                }
            }
        }
        return number;
    }
